mapper.select:
/* selectScheduleList */
	SELECT
        COUNT(1)
	FROM schedule_mst
	WHERE USEFLAG = #{useflag}

		and DIVISION	=	#{division}

 	AND CREATED_DATE BETWEEN DATE_FORMAT(CONCAT(LAST_DAY(#{frThisYear}),' 23:59:59'),'%Y-%m-%d %H:%i:%s') AND DATE_FORMAT(CONCAT(LAST_DAY(#{toThisYear}),' 23:59:59'),'%Y-%m-%d %H:%i:%s')
 	AND COMPANY_CODE = #{company_code}
 	ORDER BY SCHEDULE_SEQ ASC

/* selectScheduleDefault */ SELECT A.SCHEDULE_SEQ AS SCHEDULE_SEQ , A.DIVISION AS DIVISION , A.COMPANY_CODE AS COMPANY_CODE , A.WORK_INFO AS WORK_INFO , A.CHECK_CYCLE AS CHECK_CYCLE , A.SCHEDULE_JAN AS SCHEDULE_JAN , A.SCHEDULE_FEB AS SCHEDULE_FEB , A.SCHEDULE_MAR AS SCHEDULE_MAR , A.SCHEDULE_APR AS SCHEDULE_APR , A.SCHEDULE_MAY AS SCHEDULE_MAY , A.SCHEDULE_JUN AS SCHEDULE_JUN , A.SCHEDULE_JUL AS SCHEDULE_JUL , A.SCHEDULE_AUG AS SCHEDULE_AUG , A.SCHEDULE_SEP AS SCHEDULE_SEP , A.SCHEDULE_OCT AS SCHEDULE_OCT , A.SCHEDULE_NOV AS SCHEDULE_NOV , A.SCHEDULE_DEC AS SCHEDULE_DEC , A.ENTITY AS ENTITY , A.CONTRACT AS CONTRACT , A.FILE_SEQ AS FILE_SEQ , A.USEFLAG AS USEFLAG FROM SCHEDULE_MST A WHERE USEFLAG = #{useflag}
and DIVISION = #{division}
AND COMPANY_CODE = #{company_code} ORDER BY SCHEDULE_SEQ ASC

/* selectScheduleList */ SELECT A.SCHEDULE_SEQ AS SCHEDULE_SEQ , A.DIVISION AS DIVISION , A.COMPANY_CODE AS COMPANY_CODE , A.WORK_INFO AS WORK_INFO , A.CHECK_CYCLE AS CHECK_CYCLE , A.SCHEDULE_JAN AS SCHEDULE_JAN , A.SCHEDULE_FEB AS SCHEDULE_FEB , A.SCHEDULE_MAR AS SCHEDULE_MAR , A.SCHEDULE_APR AS SCHEDULE_APR , A.SCHEDULE_MAY AS SCHEDULE_MAY , A.SCHEDULE_JUN AS SCHEDULE_JUN , A.SCHEDULE_JUL AS SCHEDULE_JUL , A.SCHEDULE_AUG AS SCHEDULE_AUG , A.SCHEDULE_SEP AS SCHEDULE_SEP , A.SCHEDULE_OCT AS SCHEDULE_OCT , A.SCHEDULE_NOV AS SCHEDULE_NOV , A.SCHEDULE_DEC AS SCHEDULE_DEC , A.ENTITY AS ENTITY , A.CONTRACT AS CONTRACT , A.FILE_SEQ AS FILE_SEQ , A.USEFLAG AS USEFLAG FROM SCHEDULE_MST A WHERE USEFLAG = #{useflag}
and DIVISION = #{division}
AND CREATED_DATE BETWEEN DATE_FORMAT(CONCAT(LAST_DAY(#{frThisYear}),' 23:59:59'),'%Y-%m-%d %H:%i:%s') AND DATE_FORMAT(CONCAT(LAST_DAY(#{toThisYear}),' 23:59:59'),'%Y-%m-%d %H:%i:%s') AND COMPANY_CODE = #{company_code} ORDER BY SCHEDULE_SEQ ASC

