/* selectRepairList */ SELECT REPAIR_SEQ , FR_WORK , TO_WORK ,FR_IMG_ID ,(SELECT FILE_PATH FROM FILE_MST WHERE FILE_SEQ = FR_IMG_ID) AS FR_IMG_PATH ,(SELECT FILE_NAME FROM FILE_MST WHERE FILE_SEQ = FR_IMG_ID) AS FR_IMG_NAME ,TO_IMG_ID ,(SELECT FILE_PATH FROM FILE_MST WHERE FILE_SEQ = TO_IMG_ID) AS TO_IMG_PATH ,(SELECT FILE_NAME FROM FILE_MST WHERE FILE_SEQ = TO_IMG_ID) AS TO_IMG_NAME , REMARK , COMPANY_CODE , CREATED_DATE FROM REPAIR_MST WHERE USEFLAG = #{useflag} AND COMPANY_CODE = #{company_code}
AND CREATED_DATE BETWEEN #{fr_cal} AND #{to_cal}
ORDER BY REPAIR_SEQ ASC

