mapper.select:
/* "selectFileSeq" */
	SELECT  
	    FILE_SEQ
	FROM FILE_MST
	WHERE TABLE_SEQ = #{table_seq}
	AND TABLE_NAME = #{table_name}
	ORDER BY FILE_SEQ DESC LIMIT 1

/* "selectFileCnt" */
	SELECT 
	      COUNT(FILE_SEQ)
	FROM FILE_MST
	WHERE TABLE_NAME 	= #{table_name}
	AND TABLE_SEQ 		= #{table_seq}
	AND USEFLAG 		= #{useflag}
