mapper.update:

UPDATE REPORT_MST SET
	last_update_date	= SYSDATE
	,last_update_by		= #{user_name} 
	,report_title		= #{report_title}
	,report_content		= #{report_content}
WHERE report_seq		= #{report_seq}


UPDATE REPORT_MST SET
	last_update_date = SYSDATE
	,last_update_by = #{user_name} 
	,USEFLAG = 0
WHERE report_seq = #{report_seq}

mapper.insert:
Insert into REPORT_MST (
	REPORT_SEQ
	,REPORT_TITLE
	,REPORT_CONTENT
	,CREATED_DATE
	,CREATED_BY
	,LAST_UPDATE_DATE
	,LAST_UPDATE_BY
) values (
	REPORT_SEQUENCE.nextval
	, #{report_title}
	, #{report_content}
	, SYSDATE 
	, #{created_by}
	, SYSDATE
	, #{last_update_by}
)

mapper.select:
/* selectScheduleList */
	SELECT
        COUNT(1)
	FROM SCHEDULE_MST
	WHERE USEFLAG = #{useflag}

		and DIVISION	=	#{division}

 	ORDER BY SCHEDULE_SEQ ASC

/* selectScheduleList */ SELECT SCHEDULE_SEQ , DIVISION , COMPANY_CODE , COMPANY_NAME , WORK_INFO , CHECK_CYCLE , SCHEDULE_JAN , SCHEDULE_FEB , SCHEDULE_MAR , SCHEDULE_APR , SCHEDULE_MAY , SCHEDULE_JUN , SCHEDULE_JUL , SCHEDULE_AUG , SCHEDULE_SEP , SCHEDULE_OCT , SCHEDULE_NOV , SCHEDULE_DEC , ENTITY , CONTRACT , FILE_NAME
, FILE_CONTENT
, USEFLAG
FROM SCHEDULE_MST WHERE USEFLAG = #{useflag}
and DIVISION = #{division}
ORDER BY SCHEDULE_SEQ ASC

